import React, { useState, useEffect } from 'react';
import { Flex, Button, Box, Text, Stack } from '@chakra-ui/core';
import { BiHomeAlt } from 'react-icons/bi';

export default function Play(props: any) {
  const [number1, setNumber1] = useState(1);
  const [number2, setNumber2] = useState(2);
  const [level, setLevel] = useState(1);

  const generateNumbers = () => {
    const num1 = Math.floor(Math.random() * level * 2);
    const num2 = Math.floor(Math.random() * (level + 1) * 2);
    setNumber1(num1);
    setNumber2(num2);
  };

  useEffect(() => {
    generateNumbers();
  }, []);

  return (
    <>
      <Box m="20px">
        <Button
          color="gold"
          fontSize="32px"
          variant="ghost"
          _hover={{ color: 'white' }}
          _active={{ bg: 'none' }}
          _focus={{ outline: 'none' }}
          onClick={() => props.setView('start')}
        >
          <BiHomeAlt />
        </Button>
      </Box>
      <Flex width={['100%']} height={['100%']} align="center" justify="center">
        <Flex align="center" justify="center">
          <Stack spacing={5}>
            <Flex align="center" justify="center" direction="row">
              <Stack isInline>
                <Box width="100px" textAlign="center" fontSize="40px">
                  {number1}
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  +
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  {number2}
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  =
                </Box>
              </Stack>
            </Flex>
            <Flex align="center" justify="center" direction="row">
              <Input variant="unstyled" placeholder="Unstyled"></Input>
            </Flex>
          </Stack>
        </Flex>
      </Flex>
    </>
  );
}
