import React, { useState } from 'react';
import Start from './start';
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
} from '@chakra-ui/core';

export default function Main() {
  const [view, setView] = useState('start' as any);
  const { isOpen, onOpen, onClose } = useDisclosure();
  return (
    <>
      <Start setView={setView} />
      <SlideIn in={isOpen}>
        {(styles) => (
          <Modal onClose={onClose} isOpen={true}>
            <ModalOverlay opacity={styles.opacity} />
            <ModalContent pb={5} {...styles}>
              <ModalHeader>Login now</ModalHeader>
              <ModalCloseButton />
              <ModalBody>
                <Lorem count={2} />
              </ModalBody>
            </ModalContent>
          </Modal>
        )}
      </SlideIn>
    </>
  );
}
