import React, { useState, useEffect } from 'react';
import { Flex, Button, Box, Text, Stack, Input } from '@chakra-ui/core';
import { BiHomeAlt } from 'react-icons/bi';
import { useToast } from '@chakra-ui/core';

export default function Play(props: any) {
  const [number1, setNumber1] = useState(1);
  const [number2, setNumber2] = useState(2);
  const [level, setLevel] = useState(1);
  const [input, setInput] = useState('');
  const [answer, setAnswer] = useState(0);
  const toast = useToast();

  const generateNumbers = () => {
    const num1 = Math.floor(Math.random() * level * 2 + 1);
    const num2 = Math.floor(Math.random() * (level + 1) * 2 + 1);
    setNumber1(num1);
    setNumber2(num2);
    setAnswer(num1 + num2);
  };

  useEffect(() => {
    generateNumbers();
  }, []);

  const handleChange = (e: any) => {
    const tempInput = Number(e.target.value);
    if (tempInput.toString().length < answer.toString().length) {
      setInput(tempInput.toString());
    } else if (tempInput !== answer) {
      console.log('wrong');
      toast({
        duration: 500,
        render: ({ onClose }) => (
          <Box m={3} color="white" p={3} bg="red.500">
            wrong
          </Box>
        ),
      });
      setInput('');
    } else {
      toast({
        title: 'correct',
        status: 'success',
        duration: 500,
        isClosable: true,
      });
      setLevel(level + 1);
      setInput('');
      generateNumbers();
    }
  };

  return (
    <>
      <Box m="20px">
        <Button
          color="gold"
          fontSize="32px"
          variant="ghost"
          _hover={{ color: 'white' }}
          _active={{ bg: 'none' }}
          _focus={{ outline: 'none' }}
          onClick={() => props.setView('start')}
        >
          <BiHomeAlt />
        </Button>
      </Box>
      <Flex width={['100%']} height={['100%']} align="center" justify="center">
        <Flex align="center" justify="center">
          <Stack spacing={20}>
            <Flex align="center" justify="center" direction="row">
              <Stack isInline>
                <Box width="100px" textAlign="center" fontSize="40px">
                  {number1}
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  +
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  {number2}
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  =
                </Box>
              </Stack>
            </Flex>
            <Flex align="center" justify="center" direction="row">
              <Input
                autoFocus
                variant="unstyled"
                textAlign="center"
                fontSize="32px"
                type="number"
                value={input}
                onChange={(e: any) => {
                  handleChange(e);
                }}
              />
            </Flex>
          </Stack>
        </Flex>
      </Flex>
    </>
  );
}
