import React, { useState, useEffect } from 'react';
import { Flex, Button, Stack, Box, Input } from '@chakra-ui/core';

export default function Highscore(props: any) {
  const [username, setUsername] = useState('');
  const [highscores, setHighscores] = useState([]);

  const handleSave = () => {
    props.setView('home');
  };

  useEffect(() => {
    getHighscores('add');
  }, []);

  const getHighscores = (mode: string) => {
    fetch(`/api/gethighscores`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        mode,
      }),
    })
      .then((res) => res.json())
      .then((data: any) => {
        if (data.success) {
          console.log(data.data);
          setHighscores(data.data);
        }
      })
      .catch((err) => {
        console.error(
          `Something wrong happened while getting messages:${err.message}`
        );
      });
  };

  const convertDate = (date: Date) => {
    return `${new Date(date).getMonth() + 1}/${new Date(
      date
    ).getDate()}/${new Date(date).getFullYear()}`;
  };

  return (
    <>
      <Flex width={['100%']} height={['100vh']} align="center" justify="center">
        <Flex
          p={10}
          align="center"
          justify="center"
          border="5px solid gold"
          borderRadius="10px"
        >
          <Stack spacing={3}>
            <Box>Congratulations!</Box>
            <Box>You beat the high score!</Box>
            <Box>Do you want to add your username?</Box>
            <Flex align="center" justify="center" direction="row">
              <Box>{props.score}</Box>
              <Input
                autoFocus
                variant="unstyled"
                textAlign="center"
                fontSize="32px"
                type="number"
                value={username}
                onChange={(e: any) => setUsername(e.target.value)}
              />
              <Box>{convertDate(new Date())}</Box>
            </Flex>
            {highscores.map((highscore: any) => {
              return (
                <Flex
                  key={highscore._id}
                  align="center"
                  justify="space-between"
                  direction="row"
                >
                  <Box textAlign="center">{highscore.score}</Box>
                  <Box textAlign="center">{highscore.username}</Box>
                  <Box>{convertDate(highscore.createdAt)}</Box>
                </Flex>
              );
            })}
            <Flex align="center" justify="space-evenly" direction="row">
              <Button
                onClick={() => handleSave()}
                variant="outline"
                _hover={{ border: '1px solid blue' }}
                _focus={{ outline: 'none' }}
              >
                Save
              </Button>
              <Button
                onClick={() => props.setView('home')}
                variant="outline"
                _hover={{ border: '1px solid grey' }}
                _focus={{ outline: 'none' }}
              >
                Cancel
              </Button>
            </Flex>
          </Stack>
        </Flex>
      </Flex>
    </>
  );
}
