import React, { useState, useEffect } from 'react';
import { Flex, Button, Box, Text, Stack, Input } from '@chakra-ui/core';
import { BiHomeAlt } from 'react-icons/bi';
import { useToast } from '@chakra-ui/core';

export default function Play(props: any) {
  const [number1, setNumber1] = useState(1);
  const [number2, setNumber2] = useState(2);
  const [level, setLevel] = useState(1);
  const [input, setInput] = useState('');
  const [answer, setAnswer] = useState(0);
  const toast = useToast();

  const generateNumbers = () => {
    const num1 = Math.floor(Math.random() * level * 2 + 1);
    const num2 = Math.floor(Math.random() * (level + 1) * 2 + 1);
    setNumber1(num1);
    setNumber2(num2);
    setAnswer(num1 + num2);
  };

  const handleKeyDown = (e: any) => {
    if (Number(e.key) >= 0 && Number(e.key) <= 9) {
      console.log(e.key);
      handleChange(e);
    }
  };

  useEffect(() => {
    generateNumbers();
  }, []);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  });
  const handleChange = (e: any) => {
    const tempInput = Number(input.concat(e.key));
    if (tempInput.toString().length < answer.toString().length) {
      setInput(tempInput.toString());
    } else if (tempInput !== answer) {
      toast({
        position: 'top',
        duration: 500,
        render: () => (
          <Box
            m={3}
            color="blue"
            p={3}
            border="1px solid red"
            borderRadius="5px"
          >
            wrong
          </Box>
        ),
      });
      toast({
        position: 'bottom',
        duration: 2000,
        render: () => (
          <Box m={3} color="white" p={3} borderRadius="5px">
            {`Your input was ${tempInput}`}
          </Box>
        ),
      });
      setInput('');
    } else {
      toast({
        position: 'top',
        duration: 500,
        render: () => (
          <Box
            m={3}
            color="white"
            p={3}
            border="1px solid gold"
            borderRadius="5px"
          >
            correct
          </Box>
        ),
      });
      toast({
        position: 'bottom',
        duration: 2000,
        render: () => (
          <Box m={3} color="white" p={3} bg="info.500" borderRadius="5px">
            {`Your input was ${tempInput}`}
          </Box>
        ),
      });
      setLevel(level + 1);
      setInput('');
      generateNumbers();
    }
  };

  return (
    <>
      <Flex width={['100%']} align="center" justify="space-between">
        <Box m="20px">
          <Button
            color="gold"
            fontSize="32px"
            variant="ghost"
            _hover={{ color: 'white' }}
            _active={{ bg: 'none' }}
            _focus={{ outline: 'none' }}
            onClick={() => props.setView('start')}
          >
            <BiHomeAlt />
          </Button>
        </Box>
        <Flex align="center" justify="center" direction="column">
          <Box width="100px" m="20px">
            Highscore:
          </Box>
          <Box width="100px" m="20px">
            1234
          </Box>
        </Flex>
      </Flex>
      <Flex width={['100%']} height={['100%']} align="center" justify="center">
        <Flex align="center" justify="center">
          <Stack spacing={20}>
            <Flex align="center" justify="center" direction="column">
              <Box fontSize="12px" color="yellow">
                level
              </Box>
              <Box fontSize="28px" color="yellow">
                {level}
              </Box>
            </Flex>
            <Flex align="center" justify="center" direction="row">
              <Stack isInline>
                <Box width="100px" textAlign="center" fontSize="40px">
                  {number1}
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  +
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  {number2}
                </Box>
                <Box width="100px" textAlign="center" fontSize="40px">
                  =
                </Box>
              </Stack>
            </Flex>
            <Flex align="center" justify="center" direction="row">
              <Input
                autoFocus
                variant="unstyled"
                textAlign="center"
                fontSize="32px"
                type="number"
                value={input}
                onChange={(e: any) => {}}
              />
            </Flex>
          </Stack>
        </Flex>
      </Flex>
    </>
  );
}
